def iter_tree(
self,
name: str | None = None,
yield_self: bool = True,
recursive: bool = True,
level: int = 0,
count: int = 0,
assign_names: bool = True,
):
"""Yield children from bottom to top, yield self at end.
        assigns dynamic vars count, level and dynamic name
        """
if name is None:
name = self.item_name
for i, c in enumerate(self.children, start=1):
count += 1
level += 1
with_suffix = f"{name}_{i}" if len(self.children) &gt; 1 else name
if recursive:
